import React, {Component} from 'react';
import AppBarPage from "./AppBar";
import ArrowBack from '@material-ui/icons/ArrowBack';
import IconButton from "@material-ui/core/IconButton";
import withStyles from "@material-ui/core/styles/withStyles";
import TextField from '@material-ui/core/TextField';
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import 'typeface-roboto';
import Task from "./Task";

const styles = theme => ({
    backButton: {
        marginLeft: -12,
        marginRight: 20
    },
    textField: {
        marginLeft: theme.spacing.unit,
        marginRight: theme.spacing.unit
    },
    text: {
        width: '30%'
    },
    button: {
        width: '30%'
    },
    rightIcon: {
        marginLeft: theme.spacing.unit,
    }
});

class NewTask extends Component {

    constructor(props) {
        super(props);
        this.state = {
            tasks: [],
            description: '',
            responsible: '',
            status: '',
            duedate: ''
        };

        this.handleChangeDuedate = this.handleChangeDuedate.bind(this);
        this.handleChangeResponsible = this.handleChangeResponsible.bind(this);
        this.handleChangeDescription = this.handleChangeDescription.bind(this);
        this.handleChangeStatus = this.handleChangeStatus.bind(this);
        this.handleSendTask = this.handleSendTask.bind(this);
    }

    render() {


        const {classes} = this.props;

        const iconButton = (
            <IconButton
                className={classes.backButton}
                onClick={this.handleBackPage}
            >
                <ArrowBack/>
            </IconButton>
        );

        const fields = [
            {
                field: "Description",
                onchange: this.handleChangeDescription,
                type: "text"

            }, {
                field: "Responsible",
                onchange: this.handleChangeResponsible,
                type: "text"
            }, {
                field: "Status",
                onchange: this.handleChangeStatus,
                type: "text",
            }, {
                field: "Duedate",
                onchange: this.handleChangeDuedate,
                type: "date",
                default: "2019-01-01"
            }];

        const textFields = fields.map((x) => {
            return (
                <>
                    <TextField
                        required
                        label={x.field}
                        onChange={x.onchange}
                        margin="normal"
                        className={classes.text}
                        variant="outlined"
                        type={x.type}
                        defaultValue={x.default}
                    />
                    <br/>
                </>
            );
        });

        return (
            <>
                <AppBarPage button={iconButton}/>

                <br/>
                <br/>

                <Typography variant="h2" gutterBottom>
                    New Task
                </Typography>
                <form className={classes.form} onSubmit={this.handleSendTask}>

                    {textFields}

                    <Button
                        variant="contained"
                        color="primary"
                        className={classes.button}
                        type="submit"
                    >
                        Send
                    </Button>
                </form>
            </>
        );
    }

    handleBackPage() {
        window.location.href = '/tasks';
    }

    handleChangeDescription(e) {
        this.setState({description: e.target.value});
    }

    handleChangeResponsible(e) {
        this.setState({responsible: e.target.value});
    }

    handleChangeStatus(e) {
        this.setState({status: e.target.value});
    }

    handleChangeDuedate(e) {
        this.setState({duedate: e.target.value});
    }

    handleSendTask(e) {

        e.preventDefault();

        const task = () => ({
            description: this.state.description,
            responsible: this.state.responsible,
            status: this.state.status,
            duedate: this.state.duedate
        });

        const newTask = () => (
            <Task
                tasks={task}
            />
        );

        this.setState(prevState => ({
                tasks: prevState.tasks.concat(newTask)
            })
        );

        setTimeout(function () {
            var test = this.state.tasks;
            localStorage.setItem("tasks", JSON.stringify(test));
        }, 2000);
        console.log("DOG");
    }
}

export default withStyles(styles)(NewTask);
