import React, {Component} from 'react';
import AppBarPage from "./AppBar";
import ArrowBack from '@material-ui/icons/ArrowBack';
import IconButton from "@material-ui/core/IconButton";
import withStyles from "@material-ui/core/styles/withStyles";
import TextField from '@material-ui/core/TextField';
import Button from "@material-ui/core/Button";

const styles = theme => ({
    backButton: {
        marginLeft: -12,
        marginRight: 20
    },
    textField: {
        marginLeft: theme.spacing.unit,
        marginRight: theme.spacing.unit
    }
});

class NewTask extends Component {

    constructor(props) {
        super(props);
        this.state = {
            description: "",
            responsible: "",
            status: "",
            duedate: ""
        }
    }

    render() {


        const {classes} = this.props;

        const iconButton = (
            <IconButton
                className={classes.backButton}
                onClick={this.handleBackPage}
            >
                <ArrowBack/>
            </IconButton>
        );

        const fields = [
            {
                field: "Description",
                onchange: this.handleChangeDescription

            }, {
                field: "Responsible",
                onchange: this.handleChangeResponsible
            }, {
                field: "Status",
                onchange: this.handleChangeStatus
            }, {
                field: "Duedate",
                onchange: this.handleChangeDuedate
            }];

        const textFields = fields.map((x) => {
            return (
                <>
                    <TextField
                        required
                        label="User"
                        margin="normal"
                        className={classes.text}
                        variant="outlined"
                    />
                    <br/>
                </>
            );
        });

        return (
            <>
                <AppBarPage button={iconButton}/>
            </>
        )
            ;
    }

    handleBackPage() {
        window.location.href = '/tasks';
    }

    handleChangeDescription(e) {
        this.setState({description: e.target.value});
    }

    handleChangeResponsible(e) {
        this.setState({responsible: e.target.value});
    }

    handleChangeStatus(e) {
        this.setState({status: e.target.value});
    }

    handleChangeDuedate(e) {
        this.setState({duedate: e.target.value});
    }
}

export default withStyles(styles)(NewTask);
