import React, {Component} from 'react';
import Login from './Login';
import './App.css';
import {AppBarPage} from './AppBar';
import TaskPlannerDrawer from "./TaskPlannerDrawer";

class App extends Component {
    render() {

        const loginView = () => (
            <div>
                <AppBarPage/>
                <Login/>
            </div>
        );

        const taskPlannerView = () => (
            <div>
                <TaskPlannerDrawer/>
            </div>
        );

        return (
            <div className="App">
                path = {localStorage.getItem("isLoggedIn") === "true" ? 'taskplanner' : 'login'}

            </div>
        );
    }
}

export default App;
