import React, {Component} from 'react';
import IconButton from "@material-ui/core/IconButton";
import ArrowBack from '@material-ui/icons/ArrowBack';
import AppBarPage from "./AppBar";
import withStyles from "@material-ui/core/styles/withStyles";
import Typography from "@material-ui/core/Typography"
import TextField from '@material-ui/core/TextField';
import Button from "@material-ui/core/Button";

const styles = theme => ({
    backButton: {
        marginLeft: -12,
        marginRight: 20
    },
    text: {
        width: "30%"
    },
    button: {
        width: "30%"
    }
});

class UserProfile extends Component {

    constructor(props) {
        super(props);

        this.state = {
            fullname: '',
            email: '',
            phone: 0,
            occupation: '',
            password: ''
        }

        this.handleChangeFullname = this.handleChangeFullname.bind(this);
        this.handleChangeEmail = this.handleChangeEmail.bind(this);
        this.handleChangePhone = this.handleChangePhone.bind(this);
        this.handleChangeOccupation = this.handleChangeOccupation.bind(this);
        this.handleChangePassword = this.handleChangePassword.bind(this);
    }

    render() {

        const {classes} = this.props;

        const iconButton = (
            <IconButton
                className={classes.backButton}
                onClick={this.handleBackPage}
            >
                <ArrowBack/>
            </IconButton>
        );

        const fields = [
            {
                field: "Full name",
                onchange: this.handleChangeFullName,
                type: "text"
            },
            {
                field: "Your email",
                onchange: this.handleChangeEmail,
                type: "text"
            },
            {
                field: "Phone number",
                onchange: this.handleChangePhone,
                type: "number"
            },
            {
                field: "Occupation",
                onchange: this.handleChangeOccupation,
                type: "text"
            },
            {
                field: "Password",
                onchange: this.handleChangePassword,
                type: "password"
            }
        ];

        const textfields = fields.map((x) => {
            return (
                <>
                    <TextField
                        required
                        label={x.field}
                        onChange={x.onchange}
                        margin="normal"
                        className={classes.text}
                        variant="outlined"
                        type={x.type}
                        defaultValue={x.default}
                    />
                    <br/>
                </>
            );
        });

        return (
            <>
                <AppBarPage button={iconButton}/>
                <br/>
                <br/>
                <Typography variant="h2" gutterBottom>
                    Update your profile
                </Typography>
                <form className={classes.form} onSubmit={this.handleChangeProfile}>
                    {textfields}
                    <Button
                        variant="contained"
                        color="primary"
                        className={classes.button}
                        type="submit"
                    >
                        Update profile
                    </Button>
                </form>

            </>
        );
    };

    handleBackPage() {
        window.location.href = "/tasks"
    }

    handleChangeProfile(e) {

        e.preventDefault();

    }
}

export default withStyles(styles)(UserProfile);
