import React, {Component} from 'react';
import withStyles from "@material-ui/core/styles/withStyles";
import IconButton from "@material-ui/core/IconButton";
import ArrowBack from '@material-ui/icons/ArrowBack';
import AppBarPage from "./AppBar";
import Typography from "@material-ui/core/Typography";
import TextField from "./NewTask";

const styles = theme => ({
    backButton: {
        marginLeft: -12,
        marginRight: 20
    }
});

class RegisterPage extends Component {

    constructor(props) {
        super(props);

        this.state = {
            users: [],
            fullname: '',
            email: '',
            phone: 0
        }

        this.handleChangeFullName = this.handleChangeFullName.bind(this);
        this.handleChangeEmail = this.handleChangeEmail.bind(this);
        this.handleChangePhone = this.handleChangePhone.bind(this);
    }

    render() {

        const {classes} = this.props;

        const iconButton = (
            <IconButton
                className={classes.backButton}
                onClick={this.handleBackPage}
            >
                <ArrowBack/>
            </IconButton>
        );

        const fields = [
            {
                field: "Full name",
                onchange: this.handleChangeFullName,
                type: "text"
            },
            {
                field: "Your email",
                onchange: this.handleChangeEmail,
                type: "text"
            },
            {
                field: "Phone number",
                onchange: this.handleChangePhone,
                type: "number"
            }
        ];

        const textfields = fields.map((x) => {
            return (
                <>
                    <TextField
                        required
                        label={x.field}
                        onChange={x.onchange}
                        margin="normal"
                        className={classes.text}
                        variant="outlined"
                        type={x.type}
                        defaultValue={x.default}
                    />
                    <br/>
                </>
            );
        });

        return (
            <>
                <AppBarPage button={iconButton}/>

                <br/>
                <br/>

                <Typography variant="h2" gutterBottom>
                    Create your tasks account, it's free!
                </Typography>
                <form className={classes.form} onSubmit={this.handleSendRegister}>
                </form>
            </>
        );
    }

    handleBackPage() {
        window.location.href = "/login"
    }

    handleChangeFullName(e) {
        this.setState({fullname: e.target.value});
    }

    handleChangeEmail(e) {
        this.setState({email: e.target.value});
    }

    handleChangePhone(e) {
        this.setState({phone: e.target.value});
    }
}

export default withStyles(styles)(RegisterPage);
