import React, {Component} from 'react';
import AppBarPage from "./AppBar";
import ArrowBack from '@material-ui/icons/ArrowBack';
import IconButton from "@material-ui/core/IconButton";
import withStyles from "@material-ui/core/styles/withStyles";
import TextField from '@material-ui/core/TextField';
import Button from "@material-ui/core/Button";
import Icon from "@material-ui/core/Icon";

const styles = theme => ({
    backButton: {
        marginLeft: -12,
        marginRight: 20
    },
    textField: {
        marginLeft: theme.spacing.unit,
        marginRight: theme.spacing.unit
    },
    text: {
        width: '30%'
    },
    button: {
        width: '30%'
    },
    rightIcon: {
        marginLeft: theme.spacing.unit,
    }
});

class NewTask extends Component {

    constructor(props) {
        super(props);
        this.state = {
            description: "",
            responsible: "",
            status: "",
            duedate: ""
        }
    }

    render() {


        const {classes} = this.props;

        const iconButton = (
            <IconButton
                className={classes.backButton}
                onClick={this.handleBackPage}
            >
                <ArrowBack/>
            </IconButton>
        );

        const fields = [
            {
                field: "Description",
                onchange: this.handleChangeDescription,
                type: "text"

            }, {
                field: "Responsible",
                onchange: this.handleChangeResponsible,
                type: "text"
            }, {
                field: "Status",
                onchange: this.handleChangeStatus,
                type: ""
            }, {
                field: "Duedate",
                onchange: this.handleChangeDuedate,
                type: "date"
            }];

        const textFields = fields.map((x) => {
            return (
                <>
                    <TextField
                        required
                        label={x.field}

                        onChange={x.onchange}
                        margin="normal"
                        className={classes.text}
                        variant="outlined"
                        type={x.type}
                    />
                    <br/>
                </>
            );
        });

        return (
            <>
                <AppBarPage button={iconButton}/>
                {textFields}
                <Button
                    variant="contained"
                    color="primary"
                    className={classes.button}>
                    Send

                </Button>
            </>
        )
            ;
    }

    handleBackPage() {
        window.location.href = '/tasks';
    }

    handleChangeDescription(e) {
        this.setState({description: e.target.value});
    }

    handleChangeResponsible(e) {
        this.setState({responsible: e.target.value});
    }

    handleChangeStatus(e) {
        this.setState({status: e.target.value});
    }

    handleChangeDuedate(e) {
        this.setState({duedate: e.target.value});
    }
}

export default withStyles(styles)(NewTask);
